mapper.select:

     select * 
     from (select rownum rn, a.* 
           from (select * from emp order by empno) a
          ) 
	 where rn >= #{start} 
	 and   rn <= #{end}

     select * 
     from (select rownum rn, a.* 
           from (select * from emp order by empno) a
          ) 
	 where rn between #{start} and #{end}
